INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0450232','16 Blocks',2006,'Richard Donner',65664721,6.3,1062,'An aging cop is assigned the ordinary task of escorting a fast-talking witness from police custody to a courthouse, but they find themselves running the gauntlet as other forces try to prevent them from getting there.');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0074645','Hostess',1976,'Rolf Römer',0,5.0,2,'Jette and Johannes have been living together for two years when Johannes suggests that they "legalize" their relationship. Jette loves him, but the proposal of marriage terrifies her.');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0097257','Earth Girls Are Easy',1988,'Julien Temple',3916303,5.5,163,'In this musical comedy, Valerie is dealing with her philandering fiancé, Ted, when she finds that a trio of aliens have crashed their spaceship into her swimming pool. Once the furry beings are shaved at her girlfriend''s salon, the women discover three handsome men underneath. After absorbing the native culture via television, the spacemen are ready to hit the dating scene in 1980s Los Angeles.');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0130444','Aimee & Jaguar',1999,'Max Färberböck',0,7.0,42,'In 1943, while the Allies are bombing Berlin and the Gestapo is purging the capital of Jews, a dangerous love affair blossoms between two women - one a Jewish member of the underground, the other an exemplar of Nazi motherhood.');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0119791','Nightwatch',1997,'Ole Bornedal',1179002,6.1,179,'A law student, who takes a job as a night watchman at a morgue, begins to discover clues that implicate him as the suspect of a series of murders.');
