INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0261964','Without Family',2000,'Jean-Daniel Verhaeghe',0,7.3,10,'Young Remi, a foundling, lives on the farm run by his impoverished foster parents. When their money runs out, unbeknown to his foster-mother, Rémi is sold by his hard-hearted foster father to an old street performer named Vitalis. Vitalis was once a famous opera singer, but became destitute after a tragic love affair.');
