INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0084028','Monster Hunter',1981,'Joe D''Amato',0,5.4,33,'A man has been driven insane by church-sanctioned scientific experimentation which also causes him to be nearly impossible to kill. He is pursued to America by a priest where he embarks upon a killing spree while the priest tries to hunt him down and kill him.');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0079642','Erotic Nights of the Living Dead',1980,'Joe D''Amato',0,3.0,12,'A sailor takes an American businessman and his girlfriend to an island where the businessman wants to build a resort. Soon a weird voodoo couple show up and warn them of bad things that are going to happen. It doesn''t take long for the zombies to show up and start chowing down on human flesh. The main characters do manage to fit in quite a bit of sex though.');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0082479','Antropophagus',1980,'Joe D''Amato',0,5.5,57,'A group of tourists become stranded on an uninhabited island where they are stalked by an insane, violent, and grotesque killer that slaughtered the town''s former residents.');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0078916','Beyond the Darkness',1979,'Joe D''Amato',0,6.8,56,'A young rich orphan loses his fiancée to voodoo doll mischief on the part of his housekeeper who is jealous of his attentions. He digs his girlfriend up, cleans her out, stuffs her, and puts her in bed at the mansion. Following this, he tries out and disposes of a series of young maidens, trying to find the right replacement for her, and the disapproving housekeeper helps him with the disposals.');
