INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0433387','Harsh Times',2005,'David Ayer',5967038,6.5,317,'Jim Davis is an ex-Army Ranger who finds himself slipping back into his old life of petty crime after a job offer from the LAPD evaporates. His best friend is pressured by his girlfriend Sylvia to find a job, but Jim is more interested in hanging out and making cash from small heists, while trying to get a law enforcement job so he can marry his Mexican girlfriend.');
