INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0825236','Caramel',2007,'Nadine Labaki',0,6.9,99,'In a beauty salon in Beirut the lives of five women cross paths. The beauty salon is a colorful and sensual microcosm where they share and entrust their hopes, fears and expectations.');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0098532','The Match Factory Girl',1990,'Aki Kaurismäki',0,7.3,67,'Iris has a soul-deadening job as a quality-control worker watching boxes of matches go by on an assembly line all day. At night, she eats silently with her dour mother and stepfather. One weekend, wearing her new red dress at a local dance, she ends up going home with Aarne, whom she mistakenly believes is her new boyfriend. When she discovers that she''s pregnant, years of pent-up rage explode in a shocking outburst.');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0099818','I Hired a Contract Killer',1990,'Aki Kaurismäki',0,7.0,55,'After fifteen years'' service, Henri Boulanger is made redundant from his job. Shocked, he attempts suicide, but can''t go through with it, so he hires a contract killer in a seedy bar to murder him at some unspecified time in the future. But almost immediately he meets and falls in love with Margaret, a flower-seller, which makes Henri realise that his life has some meaning after all. But when he goes back to the bar to cancel the contract, he finds it has been demolished - and there''s no way he can get in touch with the killer...');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0780653','The Wolfman',2010,'Joe Johnston',0,5.7,1007,'Lawrence Talbot, an American man on a visit to Victorian London to make amends with his estranged father, gets bitten by a werewolf and, after a moonlight transformation, leaves him with a savage hunger for flesh.');
