INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt1133991','What Doesn''t Kill You',2008,'Brian Goodman',44872,6.3,73,'Two childhood friends from South Boston turn to crime as a way to get by, ultimately causing a strain in their personal lives and their friendship.');
INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0105622','Tokyo Decadence',1992,'Ryû Murakami',795312,6.2,32,'A submissive hooker goes about her trade, suffering abuse at the hands of Japanese salarymen and Yakuza types. She''s unhappy about her work, and is apparently trying to find some sort of appeasement for the fact that her lover has married.');
