INSERT INTO movies (id,title,year,director,revenue,rating,numVotes,overview) VALUES ('tt0362415','John Mayer: Any Given Thursday',2003,'',0,7.8,6,'Any Given Thursday is a live CD/DVD by John Mayer, recorded in Birmingham, Alabama at the Oak Mountain Amphitheater on September 12, 2002, during the Room for Squares tour. The album quickly peaked at #17 on the Billboard 200 chart. It features mostly songs from Room for Squares.');
